mapper.insert:
INSERT INTO t_media
SET iuser = #{iuser}
, title = #{title}
, genre = #{genre}
, date = #{date}

    , star = #{star}


    , comment = #{comment}

, isSaw = #{isSaw}

mapper.update:
UPDATE t_media
SET iuser = #{iuser}

    , title = #{title}


    , genre = #{genre}


    , date = #{date}


    , star = #{star}


    , comment = #{comment}

WHERE imedia = #{imedia}

UPDATE t_media
SET is_saw = #{isSaw}
WHERE imedia = #{imedia}
AND iuser = #{iuser}
