mapper.insert:
INSERT INTO t_media
SET iuser = #{iuser}
, title = #{title}
, genrepk = #{genrepk}
, date = #{date}

    , star = #{star}


    , comment = #{comment}

, is_saw = #{isSaw}

mapper.update:
UPDATE t_media
SET

    genrepk = #{genrepk}


    ,title = #{title}


    ,date = #{date}


    ,comment = #{comment}


    ,star = #{star}


    ,is_saw = #{isSaw}

WHERE imedia = #{imedia}
AND iuser = #{iuser}

UPDATE t_media
SET is_saw = #{isSaw}
WHERE imedia = #{imedia}
AND iuser = #{iuser}

mapper.select:
SELECT DAY(A.date) as day, B.pic
FROM t_media A
JOIN t_media_pics B
ON A.imedia = B.imedia
WHERE A.iuser = #{iuser}
AND YEAR(A.date) = #{year}
AND MONTH(A.date) = #{month}
AND del = 0

SELECT imedia, pic FROM t_media_pics
WHERE imedia in (

    #{imedia}

)

SELECT imedia
FROM t_media
WHERE iuser = #{iuser}
AND day(DATE) = #{day}
AND del = 0

SELECT imedia
FROM t_media
WHERE iuser = #{iuser}
AND imedia = #{imedia}
AND del = 0

SELECT imedia, title, comment, date, is_saw as isSaw, star, genrepk as genrePk
FROM t_media
WHERE imedia = #{imedia}
AND iuser = #{iuser}
AND del = 0
